package mum.mpp.atm;import java.awt.Frame;import java.awt.Menu;import java.awt.MenuBar;import java.awt.MenuItem;import java.awt.MenuShortcut;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import java.awt.event.WindowAdapter;import java.awt.event.WindowEvent;import mum.mpp.atm.core.ATM;import mum.mpp.atm.view.Simulation;public class ATMMain {	public static void main(String[] args) {		ATM theATM = new ATM(42, "Maharishi University of Management", "Bank of America");		Simulation theSimulation = new Simulation(theATM);		Frame mainFrame = new Frame("ATM Simulation");		mainFrame.add(theSimulation.getGUI());		MenuBar menuBar = new MenuBar();		Menu fileMenu = new Menu("File");		MenuItem quitItem = new MenuItem("Quit", new MenuShortcut('Q'));		quitItem.addActionListener(new ActionListener() {			public void actionPerformed(ActionEvent e) {				System.exit(0);			}		});		fileMenu.add(quitItem);		menuBar.add(fileMenu);		mainFrame.setMenuBar(menuBar);		mainFrame.addWindowListener(new WindowAdapter() {			public void windowClosing(WindowEvent e) {				System.exit(0);			}		});		new Thread(theATM).start();		mainFrame.setResizable(false);		mainFrame.pack();		mainFrame.setVisible(true);	}}