package mum.mpp.atm.model;import java.util.Date;import java.util.Enumeration;import mum.mpp.atm.controller.Transaction;import mum.mpp.atm.core.ATM;public abstract class Receipt {	private String[] headingPortion;	protected String[] detailsPortion;	private String[] balancesPortion;		protected Receipt(ATM atm, Card card, Transaction transaction, Balance balances) {		headingPortion = new String[4];		headingPortion[0] = new Date().toString();		headingPortion[1] = atm.getBankName();		headingPortion[2] = "ATM #" + atm.getID() + " " + atm.getPlace();		headingPortion[3] = "CARD " + card.getNumber() + " TRANS #" + transaction.getTransactionId();		balancesPortion = new String[2];		balancesPortion[0] = "TOTAL BAL: " + balances.getTotal();		balancesPortion[1] = "AVAILABLE: " + balances.getAvailable();	}	public Enumeration<String> getLines() {		return new Enumeration<String>() {			private int portion = 0;			private int index = 0;			public boolean hasMoreElements() {				return portion < 2 || index < balancesPortion.length;			}			public String nextElement() {				String line = null;				switch (portion) {				case 0:					line = headingPortion[index++];					if (index >= headingPortion.length) {						portion++;						index = 0;					}					break;				case 1:					line = detailsPortion[index++];					if (index >= detailsPortion.length) {						portion++;						index = 0;					}					break;				case 2:					line = balancesPortion[index++];					break;				}				return line;			}		};	}}