package mum.mpp.atm.view;import java.awt.GridBagConstraints;import java.awt.GridBagLayout;import java.awt.GridLayout;import java.awt.Panel;class ATMPanel extends Panel {	/**	 * 	 */	private static final long serialVersionUID = 1L;	public static final int DISPLAYABLE_LINES =     9;    public static final String BLANK_DISPLAY_LINE = "                                             ";    public static final int PRINTABLE_LINES =       9;    public static final int PRINTABLE_CHARS =      30;        private static final int DISPLAY_ROW =          0;    private static final int DISPLAY_COL =          0;    private static final int DISPLAY_WIDTH =        3;    private static final int DISPLAY_HEIGHT =       1;    private static final int DISPLAY_FILL =         GridBagConstraints.BOTH;        private static final int PRINTER_ROW =          0;    private static final int PRINTER_COL =          DISPLAY_COL + DISPLAY_WIDTH;    private static final int PRINTER_WIDTH =        1;    private static final int PRINTER_HEIGHT =       2;    private static final int PRINTER_FILL =         GridBagConstraints.NONE;        private static final int ENVELOPE_ROW =         DISPLAY_ROW + DISPLAY_HEIGHT;    private static final int ENVELOPE_COL =         0;    private static final int ENVELOPE_WIDTH =       1;    private static final int ENVELOPE_HEIGHT =      1;    private static final int ENVELOPE_FILL =        GridBagConstraints.NONE;            private static final int DISPENSER_ROW =        ENVELOPE_ROW;    private static final int DISPENSER_COL =        ENVELOPE_COL + ENVELOPE_WIDTH;    private static final int DISPENSER_WIDTH =      1;    private static final int DISPENSER_HEIGHT =     1;    private static final int DISPENSER_FILL =       GridBagConstraints.NONE;    private static final int READER_ROW =           ENVELOPE_ROW;    private static final int READER_COL =           DISPENSER_COL + DISPENSER_WIDTH;    private static final int READER_WIDTH =         1;    private static final int READER_HEIGHT =        1;    private static final int READER_FILL =          GridBagConstraints.NONE;        private static final int KEYBOARD_ROW =         READER_ROW+1;    private static final int KEYBOARD_COL =         0;    private static final int KEYBOARD_WIDTH =       DISPLAY_WIDTH;    private static final int KEYBOARD_HEIGHT =      1;    private static final int KEYBOARD_FILL =        GridBagConstraints.BOTH;    	ATMPanel(final GUI gui, SimCardReader cardReader, SimDisplay display, SimKeyboard keyboard,			SimCashDispenser cashDispenser, SimDepositEnvelopAcceptor envelopeAcceptor, SimReceiptPrinter receiptPrinter) {		GridBagLayout atmLayout = new GridBagLayout();		setLayout(atmLayout);		Panel cardReaderPanel = new Panel();		cardReaderPanel.setLayout(new GridLayout(1, 1));		cardReaderPanel.add(cardReader);		add(cardReaderPanel);		atmLayout.setConstraints(cardReaderPanel,				GUI.makeConstraints(READER_ROW, READER_COL, READER_WIDTH, READER_HEIGHT, READER_FILL));		add(display);		atmLayout.setConstraints(display,				GUI.makeConstraints(DISPLAY_ROW, DISPLAY_COL, DISPLAY_WIDTH, DISPLAY_HEIGHT, DISPLAY_FILL));		add(keyboard);		atmLayout.setConstraints(keyboard,				GUI.makeConstraints(KEYBOARD_ROW, KEYBOARD_COL, KEYBOARD_WIDTH, KEYBOARD_HEIGHT, KEYBOARD_FILL));		add(cashDispenser);		atmLayout.setConstraints(cashDispenser,				GUI.makeConstraints(DISPENSER_ROW, DISPENSER_COL, DISPENSER_WIDTH, DISPENSER_HEIGHT, DISPENSER_FILL));		Panel envelopeAcceptorPanel = new Panel();		envelopeAcceptorPanel.setLayout(new GridLayout(1, 1));		envelopeAcceptorPanel.add(envelopeAcceptor);		add(envelopeAcceptorPanel);		atmLayout.setConstraints(envelopeAcceptorPanel,				GUI.makeConstraints(ENVELOPE_ROW, ENVELOPE_COL, ENVELOPE_WIDTH, ENVELOPE_HEIGHT, ENVELOPE_FILL));		envelopeAcceptor.setVisible(false);		add(receiptPrinter);		atmLayout.setConstraints(receiptPrinter,				GUI.makeConstraints(PRINTER_ROW, PRINTER_COL, PRINTER_WIDTH, PRINTER_HEIGHT, PRINTER_FILL));	}}                                            