package mum.mpp.atm.view;import java.awt.Button;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;class SimCardReader extends Button {		/**	 * 	 */	private static final long serialVersionUID = 1L;	SimCardReader(final Simulation simulation) {		super("Click to insert card");		addActionListener(new ActionListener() {			public void actionPerformed(ActionEvent e) {				simulation.cardInserted();			}		});		setVisible(true);	}	void proceedInsertion() {		setVisible(false);	}	void proceedEjection() {		setLabel("Ejecting card");		setVisible(true);		setLabel("Click to insert card");	}	void proceedRetention() {		setLabel("Click to insert card");		setVisible(true);	}}