package mum.mpp.atm.view;import java.awt.Color;import java.awt.Font;import java.awt.GridLayout;import java.awt.Label;import java.awt.Panel;import mum.mpp.atm.model.Money;class SimCashDispenser extends Panel {	/**	 * 	 */	private static final long serialVersionUID = 1L;	private Label label;		SimCashDispenser() {		setLayout(new GridLayout(1, 1));		label = new Label("$XXX.XX", Label.CENTER);		label.setFont(new Font("SansSerif", Font.PLAIN, 18));		label.setForeground(new Color(0, 64, 0));		add(label);		label.setVisible(false);	}	public void proceedDispensingCash(Money amount) {		label.setVisible(true);		label.setText(amount.toString());		try {			Thread.sleep(1000);		} catch (InterruptedException e) {		}		label.setVisible(false);	}}